            if (!assignmentId) return;
            setLoading(true);
            try {
                const data = await getAssignmentDetailAPI(assignmentId);
                setAssignment(data.data || data);
            } catch (err: any) {
                setError(err.message || "Assignment detail load nahi ho saki");
            } finally {
                setLoading(false);
            }
        };
        fetchAssignment();
    }, [assignmentId]);

    const handleViewSubmissions = async () => {
