'use client';

import React, { useState, useMemo } from 'react';
import { SlidersHorizontal, ChevronDown, Check, Plus, Loader2, FilePlus, BookOpen, Layers } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import SearchBar from '@/components/ui/SearchBar';
import ContentCard from '@/components/courses/ContentCard'; // Jo humne pehle banaya tha

interface ModulePageTemplateProps {
    role: 'student' | 'teacher' | 'admin';
    type: 'quiz' | 'assignment' | 'resource';
    pageTitle: string;
    subTitle: string;
    allData: any[];     // Saara raw data (Lectures/Quizzes/Assignments)
    courses: any[];     // Filters ke liye courses (Assigned or Enrolled)
    onAddClick?: () => void;
}

const ModulePageTemplate: React.FC<ModulePageTemplateProps> = ({ 
    role, type, pageTitle, subTitle, allData, courses, onAddClick 
}) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [courseFilter, setCourseFilter] = useState('All');
    const [isCourseOpen, setIsCourseOpen] = useState(false);

    const isStaff = role === 'teacher' || role === 'admin';

    // 1. Dynamic Filtering Logic
    const filteredItems = useMemo(() => {
        return allData.filter(item => {
            const matchSearch = (item.title || item.name || '').toLowerCase().includes(searchTerm.toLowerCase()) || 
                                (item.courseName || '').toLowerCase().includes(searchTerm.toLowerCase());
            const matchCourse = courseFilter === 'All' || item.courseName === courseFilter;
            return matchSearch && matchCourse;
        });
    }, [allData, searchTerm, courseFilter]);

    return (
        <div className="w-full p-4 md:p-8 max-w-7xl mx-auto font-sans">
            {/* Header Section */}
            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-10 gap-4">
                <div>
                    <h1 className="text-3xl md:text-4xl font-extrabold text-[#0F172A] tracking-tight">{pageTitle}</h1>
                    <p className="text-gray-500 mt-1 font-medium">{subTitle}</p>
                </div>
                {isStaff && (
                    <button 
                        onClick={onAddClick}
                        className="flex items-center gap-2 px-6 py-3 bg-blue-600 text-white rounded-xl font-bold text-sm hover:bg-blue-700 shadow-xl shadow-blue-100 active:scale-95 transition-all w-full sm:w-auto justify-center"
                    >
                        <Plus size={20} strokeWidth={3} />
                        <span>Create New {type.charAt(0).toUpperCase() + type.slice(1)}</span>
                    </button>
                )}
            </div>

            {/* Filters Section */}
            <div className="grid grid-cols-1 md:flex items-center gap-4 mb-10">
                <div className="flex-1">
                    <SearchBar 
                        placeholder={`Search ${type} or course...`} 
                        onChange={(e: any) => setSearchTerm(e.target.value)} 
                    />
                </div>
                
                {/* Course Filter Dropdown */}
                <div className="relative min-w-[220px]">
                    <button 
                        onClick={() => setIsCourseOpen(!isCourseOpen)} 
                        className="w-full flex items-center justify-between px-5 py-3 border border-gray-100 rounded-xl bg-white text-sm font-bold text-gray-700 hover:border-blue-300 shadow-sm transition-all h-[52px]"
                    >
                        <span className="truncate">Course: {courseFilter}</span>
                        <ChevronDown size={18} className={`text-gray-400 transition-transform ${isCourseOpen ? 'rotate-180' : ''}`} />
                    </button>
                    
                    <AnimatePresence>
                        {isCourseOpen && (
                            <motion.div 
                                initial={{ opacity: 0, y: 10 }} 
                                animate={{ opacity: 1, y: 0 }} 
                                exit={{ opacity: 0, y: 10 }} 
                                className="absolute z-[60] w-full mt-2 bg-white border border-gray-50 rounded-2xl shadow-2xl py-3 max-h-72 overflow-y-auto"
                            >
                                <div 
                                    onClick={() => { setCourseFilter('All'); setIsCourseOpen(false); }}
                                    className="px-5 py-2.5 hover:bg-blue-50 cursor-pointer text-sm font-semibold flex justify-between items-center"
                                >
                                    All Courses {courseFilter === 'All' && <Check size={16} className="text-blue-600" />}
                                </div>
                                {courses.map(course => (
                                    <div 
                                        key={course.id} 
                                        onClick={() => { setCourseFilter(course.courseName || course.title); setIsCourseOpen(false); }} 
                                        className="px-5 py-2.5 hover:bg-blue-50 cursor-pointer text-sm font-semibold flex justify-between items-center"
                                    >
                                        {course.courseName || course.title} 
                                        {courseFilter === (course.courseName || course.title) && <Check size={16} className="text-blue-600" />}
                                    </div>
                                ))}
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>

                <button className="flex items-center gap-2 px-6 py-3 bg-white border border-gray-100 rounded-xl text-sm font-bold text-gray-600 hover:bg-gray-50 shadow-sm h-[52px] transition-all">
                    <SlidersHorizontal size={18} /> Sort
                </button>
            </div>

            {/* Content List */}
            <div className="space-y-4">
                {filteredItems.length > 0 ? (
                    filteredItems.map(item => (
                        <ContentCard 
                            key={item.id} 
                            id={item.id}
                            title={item.title || item.name} 
                            subtitle={item.courseName || 'General'}
                            type={type as any}
                            role={role}
                            onAction={() => alert(`${role} action on ${type}`)}
                            onEdit={() => alert(`Edit ${type}`)}
                            onDelete={() => alert(`Delete ${type}`)}
                        />
                    ))
                ) : (
                    <div className="text-center py-24 bg-white rounded-[2.5rem] border-2 border-dashed border-gray-100 shadow-inner">
                        <div className="bg-gray-50 w-20 h-20 rounded-full flex items-center justify-center mx-auto mb-6">
                            <Layers size={40} className="text-gray-200" />
                        </div>
                        <p className="text-gray-400 font-bold text-lg">No {type}s found.</p>
                        <p className="text-gray-300 text-sm">Try changing your filters or search term.</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ModulePageTemplate;

// src/components/courses/SectionWrapper.tsx
import React from 'react';
import { Plus, ChevronDown, Folder } from 'lucide-react';

interface SectionProps {
    sectionName: string;
    children: React.ReactNode;
    role: 'admin' | 'teacher' | 'student';
    type: string;
    onAddItem: () => void; // Is section mein item add karne ke liye
}

export const SectionWrapper = ({ sectionName, children, role, type, onAddItem }: SectionProps) => {
    return (
        <div className="mb-8 bg-gray-50/50 rounded-[2rem] border border-gray-100 overflow-hidden shadow-sm transition-all hover:shadow-md">
            {/* Section Header */}
            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center px-6 py-5 bg-white border-b border-gray-50 gap-4">
                <div className="flex items-center gap-3">
                    <div className="p-2.5 bg-blue-50 rounded-xl text-blue-600 shadow-sm">
                        <Folder size={20} />
                    </div>
                    <h3 className="text-lg font-extrabold text-[#0F172A] tracking-tight">{sectionName}</h3>
                </div>

                {role !== 'student' && (
                    <button 
                        onClick={onAddItem}
                        className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-xl font-bold text-xs hover:bg-blue-700 transition-all active:scale-95 shadow-lg shadow-blue-100"
                    >
                        <Plus size={16} strokeWidth={3} />
                        <span>Add {type} to Section</span>
                    </button>
                )}
            </div>

            {/* Section Content (Items List) */}
            <div className="p-4 space-y-3">
                {children}
            </div>
        </div>
    );
};

'use client';
import React from 'react';
import { UserPlus, GraduationCap, Calendar } from 'lucide-react';
import UserManagementTable from '@/components/ui/UserManagementTable';

interface StudentsTabProps {
    data: any[];
    onAdd: () => void;
    role: string;
    loading?: boolean;
    // # Naya prop add kiya gaya hai delete logic handle karne ke liye
    onDelete: (id: number, name: string) => void;
}

export const StudentsTab = ({ data, onAdd, role, onDelete, loading = false }: StudentsTabProps) => {
    if (role === 'student') return null;

    // Enrollment list ke liye Column Configuration
    const enrollmentColumns = [
        {
            header: 'Student Info',
            key: 'studentName',
            render: (item: any) => (
                <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-full bg-blue-100 text-blue-600 flex items-center justify-center font-bold uppercase shadow-sm">
                        {item.studentName?.[0] || 'S'}
                    </div>
                    <div>
                        <p className="font-bold text-sm text-[#0F172A]">{item.studentName}</p>
                    </div>
                </div>
            )
        },
        {
            header: 'Student Email',
            key: 'studentEmail',
            render: (item: any) => (
                <div className="flex items-center gap-3">
                    <p className="font-bold text-sm text-[#0F172A]">{item.studentEmail}</p>

                </div>
            )
        },
        {
            header: 'Enrollment Date',
            key: 'enrolledAt',
            align: 'center' as const,
            render: (item: any) => (
                <div className="flex flex-col items-center gap-1">
                    <div className="flex items-center gap-1.5 text-gray-500 font-bold text-xs">
                        <Calendar size={12} className="text-gray-300" />
                        {item.enrolledAt ? new Date(item.enrolledAt).toLocaleDateString('en-GB') : 'N/A'}
                    </div>
                </div>
            )
        },
        {
            header: 'Status',
            key: 'status',
            align: 'right' as const,
            render: (item: any) => (
                <span className="px-3 py-1 bg-green-50 text-green-600 rounded-full text-[10px] font-black uppercase tracking-widest shadow-sm">
                    {item.status || 'Active'}
                </span>
            )
        }
    ];

    return (
        <div className="animate-in fade-in duration-500">
            {/* Header Section */}
            <div className="flex justify-between items-center mb-8 px-2">
                <div>
                    <h2 className="text-xl font-bold text-[#0F172A]">Enrolled Students</h2>
                    <p className="text-gray-400 text-xs font-medium">List of students currently in this course</p>
                </div>

                {role === 'admin' && (
                    <button
                        onClick={onAdd}
                        className="flex items-center gap-2 px-5 py-2.5 bg-[#0F172A] text-white rounded-xl font-bold text-sm hover:bg-black transition-all shadow-lg active:scale-95"
                    >
                        <UserPlus size={18} />
                        <span>Add Student</span>
                    </button>
                )}
            </div>

            {/* Generic Table Integration */}
            <div className="bg-white rounded-3xl border border-gray-100 overflow-hidden shadow-sm">
                <UserManagementTable
                    data={data}
                    loading={loading}
                    columnConfig={enrollmentColumns}
                    type="enrollment"
                    // # Edit button ko list se remove kar diya gaya hai
                    // # Ab sirf delete aur view buttons nazar aayenge
                    visibleActions={role === 'admin' ? ['delete', 'view'] : []}
                    // # Delete confirmation logic ko integration ke liye update kiya gaya hai
                    onDelete={(id) => {
                        const student = data.find(s => s.id === id);
                        onDelete(id, student?.studentName || "Student");
                    }}
                />
            </div>

            {/* Empty State Handled by Table component already */}
        </div>
    );
};

'use client';

import React, { useState, useEffect, useMemo, use } from 'react';
import {
    Users, FileText, BookOpen, Layers, Plus, ArrowLeft,
    Loader2, Video, ClipboardList
} from 'lucide-react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';

// API aur Components Imports
import {
    getCourseWithContentAPI,
    getStudentsAPI,
    createSectionAPI,
    createResourceAPI,
    createRecordedLectureAPI,
    dismissStudentAPI,
    createLiveLectureAPI,
    createAssignmentAPI,
    enrollStudentAPI,
    deleteLectureAPI,
    updateLectureAPI,
    deleteResourceAPI,
    updateResourceAPI,
    createQuizAPI,
    updateQuizAPI,
    deleteQuizAPI
} from '@/lib/api/apiService';
import { CourseInfoCard } from '@/components/courses/CourseInfoCard';
import { StudentsTab } from '@/components/courses/StudentsTab';
import { GenericContentTab } from '@/components/courses/GenericContentTab';
import GenericFormModal, { FormField } from '@/components/ui/GenericFormModal';
import DeleteConfirmationModal from '@/components/ui/DeleteConfirmationModal';

// # PROFESSIONAL MAPPING TO FIX "QUIZZE" ERROR
const TAB_TO_TYPE_MAP: Record<string, ModalType> = {
    students: 'student',
    lectures: 'lecture',
    quizzes: 'quiz',
    assignments: 'assignment',
    resources: 'resource'
};

type ModalType = 'student' | 'lecture' | 'quiz' | 'assignment' | 'resource' | 'section';

const UnifiedCourseDetail = ({ params, role }: { params: Promise<{ courseId?: string; id?: string }>, role: 'admin' | 'teacher' }) => {
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const actualParams = use(params) as any;
    const courseId = actualParams.courseId || actualParams.id;

    const [activeTab, setActiveTab] = useState('students');
    const [activeLectureSubTab, setActiveLectureSubTab] = useState<'recorded' | 'online'>('recorded');

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const [fullData, setFullData] = useState<any>(null);
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const [allAvailableStudents, setAllAvailableStudents] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
    const [modalLoading, setModalLoading] = useState(false);
    const [modalType, setModalType] = useState<ModalType>('lecture');
    const [selectedSectionId, setSelectedSectionId] = useState<number | null>(null);

    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const [itemToEdit, setItemToEdit] = useState<any>(null);
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    const [itemToDelete, setItemToDelete] = useState<any>(null);

    const tabs = [
        { id: 'students', label: 'Students', icon: Users },
        { id: 'lectures', label: 'Lectures', icon: Video },
        { id: 'quizzes', label: 'Quizzes', icon: FileText },
        { id: 'assignments', label: 'Assignments', icon: ClipboardList },
        { id: 'resources', label: 'Resources', icon: Layers },
    ];

    const fetchData = async () => {
        setLoading(true);
        try {
            const contentRes = await getCourseWithContentAPI(Number(courseId));
            setFullData(contentRes);

            if (role === 'admin') {
                try {
                    const studentsRes = await getStudentsAPI();
                    const rawStudents = studentsRes.data || [];
                    // eslint-disable-next-line @typescript-eslint/no-explicit-any
                    setAllAvailableStudents(rawStudents.map((s: any) => ({
                        label: `${s.firstName} ${s.lastName} (${s.email})`,
                        value: s.id
                    })));
                } catch (studentErr) {
                    console.error("Admin student list fetch error:", studentErr);
                }
            }
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
        } catch (err: any) {
            console.error("Main Content Fetch Error:", err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => { fetchData(); }, [courseId]);

    const activeTabData = useMemo(() => {
        if (!fullData?.sections) return [];
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        return fullData.sections.map((sec: any) => ({
            id: sec.id,
            sectionName: sec.title,
            items: sec[activeTab] || []
        }));
    }, [fullData, activeTab]);

    const formFields = useMemo(() => {
        const lectureTypeToUse = itemToEdit ? itemToEdit.lectureType : activeLectureSubTab;

        const configs: Record<string, FormField[]> = {
            section: [
                { name: 'title', label: 'Section Title', type: 'text', required: true },
                { name: 'description', label: 'Brief Description', type: 'textarea' }
            ],
            lecture: lectureTypeToUse === 'online' ? [
                { name: 'title', label: 'Session Title', type: 'text', required: true },
                { name: 'description', label: 'Agenda/Description', type: 'textarea' },
                { name: 'liveStart', label: 'Start Date & Time', type: 'datetime-local', required: true },
                { name: 'lectureOrder', label: 'Order', type: 'number', required: true }
            ] : [
                { name: 'title', label: 'Lecture Title', type: 'text', required: true },
                { name: 'description', label: 'Description', type: 'textarea' },
                { name: 'video', label: 'Video File', type: 'files', required: !itemToEdit },
                { name: 'lectureOrder', label: 'Order', type: 'number', required: true }
            ],
            assignment: [
                { name: 'title', label: 'Assignment Title', type: 'text', required: true },
                { name: 'objective', label: 'Objective', type: 'textarea' },
                { name: 'deliverable', label: 'Deliverable description', type: 'textarea' },
                { name: 'totalMarks', label: 'Total Marks', type: 'number' },
                { name: 'dueDate', label: 'Due Date', type: 'date' },
                { name: 'file', label: 'Attachment', type: 'files', required: false }
            ],
            resource: [
                { name: 'title', label: 'Resource Title', type: 'text', required: true },
                { name: 'resourceType', label: 'Type', type: 'select', options: [{ label: 'PDF', value: 'pdf' }, { label: 'Video', value: 'video' }], required: true },
                { name: 'file', label: 'Upload File', type: 'files', required: !itemToEdit }
            ],
            student: [{ name: 'studentId', label: 'Select Student', type: 'select', options: allAvailableStudents, required: true }],
            // # QUIZ FORM FIELDS WITH DYNAMIC BUILDER
            quiz: [
                { name: 'title', label: 'Quiz Title', type: 'text', required: true },
                { name: 'description', label: 'Quiz Description', type: 'textarea' },
                { name: 'total_marks', label: 'Total Marks', type: 'number', required: true },
                { name: 'start_time', label: 'Start Time', type: 'datetime-local', required: false },
                { name: 'end_time', label: 'End Time', type: 'datetime-local', required: false },
                { name: 'is_Published', label: 'Publish Immediately?', type: 'select', options: [{ label: 'Yes', value: 'true' }, { label: 'No', value: 'false' }] },
                { name: 'questions', label: 'Quiz Questions', type: 'quiz-builder', required: true }
            ],
        };
        return configs[modalType] || [];
    }, [modalType, activeLectureSubTab, allAvailableStudents, itemToEdit]);

    const handleFormSubmit = async (formData: FormData) => {
        setModalLoading(true);
        try {
            const courseIdNum = Number(courseId);
            const rawData = Object.fromEntries(formData);

            if (itemToEdit) {
                if (modalType === 'quiz') {
                    const payload = {
                        ...rawData,
                        questions: JSON.parse(rawData.questions as string),
                        total_marks: Number(rawData.total_marks),
                        is_Published: rawData.is_Published === 'true'
                    };
                    await updateQuizAPI(itemToEdit.id, payload);
                }
                else if (modalType === 'lecture') await updateLectureAPI(itemToEdit.id, courseIdNum, formData);
                else if (modalType === 'resource') await updateResourceAPI(courseIdNum, selectedSectionId!, itemToEdit.id, formData);
                alert("Kamyabi se update ho gaya!");
            } else {
                if (modalType === 'section') await createSectionAPI(courseIdNum, { title: rawData.title as string, description: rawData.description as string });
                else if (modalType === 'assignment') {
                    formData.append('courseId', courseId.toString());
                    formData.append('sectionId', selectedSectionId!.toString());
                    await createAssignmentAPI(formData);
                }
                else if (modalType === 'student') await enrollStudentAPI({ courseId: courseIdNum, studentId: Number(rawData.studentId) });

                // # CREATE QUIZ LOGIC
                else if (modalType === 'quiz') {
                    await createQuizAPI({
                        ...rawData,
                        course_id: courseIdNum,
                        section_id: selectedSectionId,
                        total_marks: Number(rawData.total_marks),
                        is_Published: rawData.is_Published === 'true',
                        questions: JSON.parse(rawData.questions as string)
                    });
                }
                else if (modalType === 'lecture') {
                    if (activeLectureSubTab === 'online') {
                        await createLiveLectureAPI({
                            title: rawData.title, description: rawData.description,
                            courseId: courseIdNum, sectionId: selectedSectionId,
                            liveStart: new Date(rawData.liveStart as string).toISOString(),
                            lectureOrder: Number(rawData.lectureOrder)
                        });
                    } else {
                        formData.append('courseId', courseId.toString());
                        formData.append('sectionId', selectedSectionId!.toString());
                        await createRecordedLectureAPI(formData);
                    }
                }
                else if (modalType === 'resource') await createResourceAPI(courseIdNum, selectedSectionId!, formData);
                alert("Naya content add ho gaya!");
            }

            await fetchData();
            setIsModalOpen(false);
            setItemToEdit(null);
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
        } catch (err: any) { alert(err.message || "Operation fail ho gaya"); }
        finally { setModalLoading(false); }
    };

    const handleDeleteConfirm = async () => {
        if (!itemToDelete) return;
        setModalLoading(true);
        try {
            const courseIdNum = Number(courseId);
            const itemId = Number(itemToDelete.id);

            if (itemToDelete.type === 'enrollment') await dismissStudentAPI(itemId, courseIdNum, itemToDelete.studentId);
            else if (itemToDelete.type === 'quiz') await deleteQuizAPI(itemId);
            else if (itemToDelete.type === 'lecture') await deleteLectureAPI(itemId, courseIdNum);
            else if (itemToDelete.type === 'resource') await deleteResourceAPI(courseIdNum, itemToDelete.sectionId, itemId);

            setIsDeleteModalOpen(false);
            await fetchData(); // Refresh fresh state
            alert("Item permanent delete ho gaya!");
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
        } catch (err: any) { alert(err.message); }
        finally { setModalLoading(false); setItemToDelete(null); }
    };

    if (loading && !fullData) return <div className="h-screen flex items-center justify-center bg-gray-50"><Loader2 className="animate-spin text-blue-600" size={48} /></div>;

    return (
        <div className="w-full bg-gray-50 min-h-screen font-sans text-[#0F172A] pt-0">
            <div className="flex justify-between items-center mb-4 p-8 pb-0">
                <Link href={role === 'admin' ? "/admin/courses" : "/teacher/assigned-courses"} className="flex items-center gap-2 text-slate-400 hover:text-blue-600 font-bold transition-all text-sm uppercase tracking-widest">
                    <ArrowLeft size={16} /> Back to Dashboard
                </Link>
            </div>

            <div className="p-8 pt-4">
                <CourseInfoCard data={fullData?.course} />

                <div className="flex gap-2 bg-white p-1.5 rounded-2xl border border-slate-100 my-6 overflow-x-auto no-scrollbar shadow-sm">
                    {tabs.map((tab) => (
                        <button key={tab.id} onClick={() => setActiveTab(tab.id)} className={`flex items-center gap-2 px-6 py-3 rounded-xl text-xs font-black uppercase tracking-widest transition-all ${activeTab === tab.id ? 'bg-blue-600 text-white shadow-lg' : 'text-slate-400 hover:bg-slate-50'}`}>
                            <tab.icon size={18} /> {tab.label}
                        </button>
                    ))}
                </div>

                <div className="bg-white rounded-[2.5rem] shadow-sm border border-slate-100 p-8 min-h-[450px]">
                    {activeTab === 'students' ? (
                        <StudentsTab
                            data={fullData?.enrollments || []}
                            role={role}
                            onDelete={(id, name) => {
                                setItemToDelete({ id, title: name, type: 'enrollment' });
                                setIsDeleteModalOpen(true);
                            }}
                            onAdd={() => { setModalType('student'); setItemToEdit(null); setIsModalOpen(true); }}
                        />
                    ) : (
                        <GenericContentTab
                            title={activeTab}
                            // # USE MAPPING FOR TYPE TO FIX "QUIZZE" ERROR
                            // eslint-disable-next-line @typescript-eslint/no-explicit-any
                            type={TAB_TO_TYPE_MAP[activeTab] as any}
                            data={activeTabData}
                            role={role}
                            onAddSection={() => { setModalType('section'); setItemToEdit(null); setIsModalOpen(true); }}
                            // eslint-disable-next-line @typescript-eslint/no-explicit-any
                            onSubTabChange={(tab: any) => setActiveLectureSubTab(tab)}
                            onAddItem={(sectionId: number) => {
                                setSelectedSectionId(sectionId);
                                setModalType(TAB_TO_TYPE_MAP[activeTab]);
                                setItemToEdit(null);
                                setIsModalOpen(true);
                            }}
                            // eslint-disable-next-line @typescript-eslint/no-explicit-any
                            onEditItem={(item: any, sectionId: number) => {
                                // # PRE-FILL FIX: Ensuring nested data (questions) is passed to modal
                                console.log("Editing Item Data:", item);
                                setItemToEdit(item);
                                setSelectedSectionId(sectionId);
                                setModalType(TAB_TO_TYPE_MAP[activeTab]);
                                setIsModalOpen(true);
                            }}
                            // eslint-disable-next-line @typescript-eslint/no-explicit-any
                            onDeleteItem={(item: any, sectionId: number) => {
                                setItemToDelete({ ...item, type: TAB_TO_TYPE_MAP[activeTab], sectionId });
                                setIsDeleteModalOpen(true);
                            }}
                        />
                    )}
                </div>
            </div>

            <GenericFormModal
                isOpen={isModalOpen}
                onClose={() => { setIsModalOpen(false); setItemToEdit(null); }}
                title={itemToEdit ? `Update ${modalType}` : `Add New ${modalType}`}
                submitText={itemToEdit ? "Update Changes" : "Save Changes"}
                fields={formFields}
                onSubmit={handleFormSubmit}
                loading={modalLoading}
                // # ENSURING INITIAL DATA IS SPREAD CORRECTLY
                initialData={itemToEdit}
            />

            <DeleteConfirmationModal
                isOpen={isDeleteModalOpen}
                onClose={() => setIsDeleteModalOpen(false)}
                onConfirm={handleDeleteConfirm}
                title={itemToDelete?.title || "Item"}
                loading={modalLoading}
            />
        </div>
    );
};

